import React, { useState, useEffect } from 'react';

function Documentos2() {
  const [documentosMerma, setDocumentosMerma] = useState([]);
  const [documentosFacturas, setDocumentosFacturas] = useState([]);
  const [documentosCerradas, setDocumentosCerradas] = useState([]);

  // Cargar documentos previos desde el almacenamiento local
  useEffect(() => {
    const documentosGuardadosMerma = JSON.parse(localStorage.getItem('documentosMerma')) || [];
    const documentosGuardadosFacturas = JSON.parse(localStorage.getItem('documentosFacturas')) || [];
    const documentosGuardadosCerradas = JSON.parse(localStorage.getItem('documentosCerradas')) || [];

    console.log('Documentos Merma:', documentosGuardadosMerma); // Verifica qué documentos están en localStorage
    console.log('Documentos Facturas:', documentosGuardadosFacturas);
    console.log('Documentos Cerradas:', documentosGuardadosCerradas);

    setDocumentosMerma(documentosGuardadosMerma);
    setDocumentosFacturas(documentosGuardadosFacturas);
    setDocumentosCerradas(documentosGuardadosCerradas);
  }, []);

  // Filtrar documentos según el correo
  const filtrarDocumentosPorCorreo = (documentos) => {
    return documentos.filter(doc => 
      doc.correo && (doc.correo.endsWith('@conductorR.cl') || doc.correo.endsWith('@conductorAV.cl'))
    );
  };

  return (
    <div className="fondo">
      <div className="contenedores">
        {/* Contenedor Merma */}
        <div className="contenedor">
          <h3 className="text-lg font-semibold">Merma cliente</h3>
          <div>
            <h4>Documentos cargados:</h4>
            <ul>
              {filtrarDocumentosPorCorreo(documentosMerma).length === 0 ? (
                <p>No se encontraron documentos.</p>
              ) : (
                filtrarDocumentosPorCorreo(documentosMerma).map((doc, index) => (
                  <li key={index}>
                    <a href={doc.url} download={doc.nombre} target="_blank" rel="noopener noreferrer">
                      {doc.nombre}
                    </a>
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>

        {/* Contenedor Facturas */}
        <div className="contenedor">
          <h3 className="text-lg font-semibold">Facturas con diferencia</h3>
          <div>
            <h4>Documentos cargados:</h4>
            <ul>
              {filtrarDocumentosPorCorreo(documentosFacturas).length === 0 ? (
                <p>No se encontraron documentos.</p>
              ) : (
                filtrarDocumentosPorCorreo(documentosFacturas).map((doc, index) => (
                  <li key={index}>
                    <a href={doc.url} download={doc.nombre} target="_blank" rel="noopener noreferrer">
                      {doc.nombre}
                    </a>
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>

        {/* Contenedor Cerradas */}
        <div className="contenedor">
          <h3 className="text-lg font-semibold">Facturas cerradas</h3>
          <div>
            <h4>Documentos cargados:</h4>
            <ul>
              {filtrarDocumentosPorCorreo(documentosCerradas).length === 0 ? (
                <p>No se encontraron documentos.</p>
              ) : (
                filtrarDocumentosPorCorreo(documentosCerradas).map((doc, index) => (
                  <li key={index}>
                    <a href={doc.url} download={doc.nombre} target="_blank" rel="noopener noreferrer">
                      {doc.nombre}
                    </a>
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Documentos2;
