import React, { useState, useEffect } from 'react';
import { GoogleMap, DirectionsService, DirectionsRenderer, useJsApiLoader } from '@react-google-maps/api';

const MapaRuta = () => {
  const [directions, setDirections] = useState(null);
  const origin = { lat: -38.73965, lng: -72.58948 };  // Punto de origen (temporal)
  const destination = { lat: -38.73559, lng: -72.59386 };  // Punto de destino (temporal)

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'TU_API_KEY_AQUI',  // Asegúrate de reemplazarlo con tu clave de API de Google Maps
  });

  useEffect(() => {
    if (isLoaded) {
      const directionsService = new window.google.maps.DirectionsService();

      // Solicitar dirección entre el origen y el destino
      directionsService.route(
        {
          origin: origin,  // Origen
          destination: destination,  // Destino
          travelMode: window.google.maps.TravelMode.DRIVING,  // Puedes cambiar a WALKING, BICYCLING, etc.
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {
            setDirections(result);  // Si la solicitud es exitosa, guarda la ruta
          } else {
            console.error('Error al obtener la dirección: ', result);
          }
        }
      );
    }
  }, [isLoaded]);  // Solo se ejecutará cuando el mapa se haya cargado

  // Estilos del mapa
  const containerStyle = {
    width: '100%',
    height: '600px',  // Tamaño del mapa
  };

  const center = {
    lat: (origin.lat + destination.lat) / 2,  // Centro entre origen y destino
    lng: (origin.lng + destination.lng) / 2,
  };

  if (!isLoaded) return <div>Cargando mapa...</div>;  // Mensaje mientras se carga el mapa

  return (
    <div className="mapa-ruta">
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={14}>
        {/* Renderiza la ruta si tenemos una */}
        {directions && <DirectionsRenderer directions={directions} />}
      </GoogleMap>
    </div>
  );
};

export default MapaRuta;
