import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, DirectionsRenderer, useJsApiLoader } from '@react-google-maps/api';

const RutaDespacho = () => {
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [routeStarted, setRouteStarted] = useState(false); // Para manejar cuando iniciar la ruta
  const [origin] = useState('Temuco, Chile'); // Origen de la ruta
  const [destination] = useState('Pucón, Chile'); // Destino de la ruta (ya no necesitas setDestination)

  // Carga de la API de Google Maps
  const { isLoaded, google } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyCcF0vvhLLmuLAJcnh2ElM9aLcIwtiiwMo', // Usamos tu API Key
    libraries: ['places', 'directions'], // Asegúrate de que sólo se carguen las librerías necesarias
  });

  // Función para calcular la ruta
  const calculateRoute = useCallback(() => {
    if (!origin || !destination || !google) return;

    const directionsService = new google.maps.DirectionsService();

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === 'OK') {
          setDirectionsResponse(result); // Almacena la respuesta si la ruta fue exitosa
        } else {
          console.error('Error al calcular la ruta: ', status);
        }
      }
    );
  }, [origin, destination, google]);

  // Inicia el cálculo de la ruta
  const handleStartRoute = () => {
    setRouteStarted(true); // Marca que la ruta ha sido iniciada
  };

  useEffect(() => {
    if (isLoaded && google && routeStarted) {
      calculateRoute(); // Calcula la ruta una vez que todo esté cargado
    }
  }, [isLoaded, google, routeStarted, calculateRoute]);

  if (!isLoaded) return <div>Cargando mapa...</div>;

  return (
    <div className="ruta-despacho-container">
      <h2>Ruta de Despacho</h2>
      <button onClick={handleStartRoute}>Iniciar Ruta</button>

      <GoogleMap
        mapContainerStyle={{ width: '100%', height: '500px' }}
        center={{ lat: -38.73965, lng: -72.58948 }}
        zoom={12}
      >
        {directionsResponse && routeStarted && (
          <DirectionsRenderer
            directions={directionsResponse}
            options={{
              suppressMarkers: true, // Evitar que Google Maps muestre marcadores predeterminados
              polylineOptions: {
                strokeColor: '#FF0000', // Color de la ruta
                strokeWeight: 5, // Grosor de la línea de la ruta
              },
            }} 
          />
        )}
      </GoogleMap>

      <div className="ruta-info">
        <h3>Detalles de la Ruta:</h3>
        <ul>
          <li><strong>Origen:</strong> {origin}</li>
          <li><strong>Destino:</strong> {destination}</li>
        </ul>

        {directionsResponse && routeStarted && (
          <div>
            <h3>Instrucciones de la Ruta:</h3>
            <ul>
              {directionsResponse.routes[0].legs[0].steps.map((step, index) => (
                <li key={index}>
                  <strong>{step.distance.text}:</strong> {step.instructions}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default RutaDespacho;
