import './App.css';
import { useState, useEffect } from 'react';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import MenuNavegacion from './components/MenuNavegacion';
import MapaNavegacionCliente from './components/MapaNavegacionCliente';
import Inicio from './components/Inicio';
import Vehiculos from './components/Vehiculos';
import Doc from './components/documento';
import Doc2 from './components/documento2';
import Perfil from './components/Perfil';
import Login from './components/login';
import Registro from './components/registro';
import Logout from './components/logout'; 
import MantencionVehiculos from './components/mantencion'; 

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState(null); // Estado para almacenar el correo electrónico del usuario

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const email = localStorage.getItem('userEmail');  // Obtener el correo electrónico desde localStorage

    // Si hay un token y un correo, se considera que el usuario está autenticado
    if (token && email) {
      setIsAuthenticated(true);
      setUserEmail(email);  // Establecer el correo electrónico
    }
  }, []);  // Este useEffect solo se ejecuta una vez cuando el componente se monta

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userName');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('usuarioRegistrado');
    setIsAuthenticated(false);
    setUserEmail(null);  // Limpiar el correo electrónico al cerrar sesión

    window.location.href = '/dashboard/inicio';  // Redirige a la pantalla de inicio
  };

  return (
    <Router>
      <MenuNavegacion 
        isAuthenticated={isAuthenticated} 
        handleLogout={handleLogout} 
        userEmail={userEmail}  // Pasar el correo electrónico al componente MenuNavegacion
      />
      <Routes>
        <Route path="/dashboard/mantencion" element={<MantencionVehiculos />} />
        <Route path="/dashboard/mapa" element={<MapaNavegacionCliente setIsAuthenticated={setIsAuthenticated}/>} />
        <Route path="/dashboard/vehiculos" element={<Vehiculos setIsAuthenticated={setIsAuthenticated}/>} />
        <Route path="/dashboard/documento" element={<Doc />} />
        <Route path="/dashboard/documento2" element={<Doc2 />} />

        {/* Rutas que requieren autenticación */}
        <Route path="/dashboard/perfil" element={isAuthenticated ? <Perfil /> : <Navigate to="/dashboard/login" />} />
        <Route path="/dashboard/inicio" element={<Inicio />} />

        {/* Rutas de Login y Registro */}
        <Route path="/dashboard/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/dashboard/registro" element={<Registro setIsAuthenticated={setIsAuthenticated} />} /> 
        <Route path="/dashboard/logout" element={<Logout setIsAuthenticated={setIsAuthenticated} />} />
      </Routes>
    </Router>
  );
}

export default App;
