import React, { useState, useEffect } from 'react';

function ArchivosSubidos() {
  const [archivos, setArchivos] = useState([]);

  useEffect(() => {
    // Simulando la carga de archivos desde una fuente externa (puedes cambiar esto para obtener datos reales)
    const archivosGuardados = JSON.parse(localStorage.getItem('archivosSubidos')) || [];
    setArchivos(archivosGuardados);
  }, []);

  const handleDownload = (archivo) => {
    // Descargar el archivo
    const link = document.createElement('a');
    link.href = archivo.url;
    link.download = archivo.nombre;
    link.click();
  };

  const handleVisualizar = (archivo) => {
    // Mostrar archivo (si es PDF o imagen, por ejemplo)
    window.open(archivo.url, '_blank');
  };

  return (
    <div className="fondo">
      <div className="contenedores">
        <h3 className="text-lg font-semibold">Archivos Subidos</h3>
        <div className="archivos-container">
          {archivos.length > 0 ? (
            archivos.map((archivo, index) => (
              <div key={index} className="archivo-card">
                <h4>{archivo.nombre}</h4>
                <div className="acciones">
                  <button
                    onClick={() => handleVisualizar(archivo)}
                    className="btn-ver"
                  >
                    Ver
                  </button>
                  <button
                    onClick={() => handleDownload(archivo)}
                    className="btn-descargar"
                  >
                    Descargar
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p>No se han subido archivos a√∫n.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ArchivosSubidos;
