import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { updateUserProfile } from '../services/userService'; // Suponiendo que tienes esta función para actualizar los datos

function Perfil({ setIsAuthenticated, setUserEmail }) {
  const [nombre, setNombre] = useState('');
  const [rut, setRut] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isEditing, setIsEditing] = useState(false); // Controla si los campos son editables
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const userEmail = localStorage.getItem('userEmail');
    if (!token || !userEmail) {
      navigate('/login');  // Redirige al login si no hay sesión activa
    } else {
      // Cargar los datos del usuario desde el localStorage
      setEmail(localStorage.getItem('userEmail'));
      setNombre(localStorage.getItem('userNombre'));
      setRut(localStorage.getItem('userRut') || ''); // Si no hay rut, poner vacío
    }
  }, [navigate]);

  // Maneja los cambios en los inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case 'nombre':
        setNombre(value);
        break;
      case 'rut':
        setRut(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'password':
        setPassword(value);
        break;
      case 'confirmPassword':
        setConfirmPassword(value);
        break;
      default:
        break;
    }
  };

  // Maneja la actualización del perfil
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validación de la contraseña
    if (password && password !== confirmPassword) {
      setMessage('Las contraseñas no coinciden');
      return;
    }

    // Crear el objeto de actualización con las propiedades opcionales
    const updateData = { nombre, rut, email, password: password || undefined };

    try {
      const response = await updateUserProfile(updateData);
      setMessage(response.data.message || 'Perfil actualizado correctamente');

      if (response.data.token) {
        localStorage.setItem('authToken', response.data.token);  // Actualiza el token
      }

      // Redirige a una página de perfil o de éxito después de la actualización
      navigate('/perfil'); // Esto redirige al usuario a su perfil

    } catch (error) {
      console.error('Error al actualizar perfil:', error);
      setMessage('Hubo un error al actualizar el perfil');
    }
  };

  // Maneja la edición del perfil
  const toggleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Manejo de la salida de sesión
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userNombre');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('userRut');
    setIsAuthenticated(false);
    navigate('/login');
  };

  return (
    <div className="perfil-container">
      <h2>Perfil de Usuario</h2>

      {message && <div className="alert">{message}</div>}  {/* Mensaje de alerta si hay algún error o éxito */}

      <form onSubmit={handleSubmit} className="perfil-form">
        <div className="perfil-group">
          <label>Nombre</label>
          <input
            type="text"
            name="nombre"
            value={nombre}
            onChange={handleChange}
            disabled={!isEditing}
            required
            className="perfil-input"
          />
        </div>

        <div className="perfil-group">
          <label>Correo electrónico</label>
          <input
            type="email"
            name="email"
            value={email}
            onChange={handleChange}
            disabled
            required
            className="perfil-input"
          />
        </div>

        <div className="perfil-group">
          <label>RUT</label>
          <input
            type="text"
            name="rut"
            value={rut}
            onChange={handleChange}
            disabled={!isEditing}
            required
            className="perfil-input"
          />
        </div>

        {isEditing && (
          <>
            <div className="perfil-group">
              <label>Contraseña</label>
              <input
                type="password"
                name="password"
                value={password}
                onChange={handleChange}
                className="perfil-input"
              />
            </div>

            <div className="perfil-group">
              <label>Confirmar Contraseña</label>
              <input
                type="password"
                name="confirmPassword"
                value={confirmPassword}
                onChange={handleChange}
                className="perfil-input"
              />
            </div>
          </>
        )}

        <div className="perfil-buttons">
          <button type="submit" className="perfil-btn" disabled={!isEditing}>Guardar Cambios</button>
          <button type="button" className="perfil-btn" onClick={toggleEdit}>
            {isEditing ? 'Cancelar' : 'Editar Perfil'}
          </button>
          <button type="button" className="perfil-btn" onClick={handleLogout}>
            Cerrar Sesión
          </button>
        </div>
      </form>
    </div>
  );
}

export default Perfil;
