import React, { useState, useEffect } from 'react';
import { GoogleMap, DirectionsService, DirectionsRenderer, TrafficLayer, useJsApiLoader } from '@react-google-maps/api';

const RutaDespacho = () => {
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [origin, setOrigin] = useState('Temuco, Chile');  // Origen de la ruta
  const [destination, setDestination] = useState('Pucón, Chile'); // Destino de la ruta
  const [waypoints, setWaypoints] = useState([
    { location: 'Lican Ray, Chile', stopover: true },  // Parada intermedia 1
    { location: 'Villarrica, Chile', stopover: true }, // Parada intermedia 2
  ]);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'TU_GOOGLE_MAPS_API_KEY', // Asegúrate de tener tu clave API
  });

  const calculateRoute = () => {
    if (!origin || !destination) return;

    const directionsService = new google.maps.DirectionsService();

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        waypoints: waypoints,
        travelMode: google.maps.TravelMode.DRIVING,
        transitOptions: {
          departureTime: new Date(),
        },
        unitSystem: google.maps.UnitSystem.METRIC,
        optimizeWaypoints: true, // Optimizar la ruta en base al tráfico
      },
      (result, status) => {
        if (status === 'OK') {
          setDirectionsResponse(result);
        } else {
          console.error('Error al calcular la ruta: ', status);
        }
      }
    );
  };

  useEffect(() => {
    if (isLoaded) {
      calculateRoute();
    }
  }, [isLoaded]);

  if (!isLoaded) return <div>Cargando mapa...</div>;

  return (
    <div className="ruta-despacho-container">
      <h2>Ruta de Despacho</h2>
      <GoogleMap
        mapContainerStyle={{ width: '100%', height: '500px' }}
        center={{ lat: -38.73965, lng: -72.58948 }} // Centrado inicial
        zoom={12}
      >
        {/* Capa de tráfico */}
        <TrafficLayer autoUpdate />

        {/* Mostrar la ruta si se calculó correctamente */}
        {directionsResponse && (
          <DirectionsRenderer
            directions={directionsResponse}
            options={{
              suppressMarkers: true,  // Suprimir los marcadores predeterminados
              polylineOptions: {
                strokeColor: '#FF0000',  // Color de la línea de la ruta
                strokeWeight: 5,  // Grosor de la línea
              },
            }}
          />
        )}
      </GoogleMap>

      {/* Detalles de la ruta */}
      <div className="ruta-info">
        <h3>Detalles de la Ruta:</h3>
        <ul>
          <li><strong>Origen:</strong> {origin}</li>
          <li><strong>Destino:</strong> {destination}</li>
          <li><strong>Paradas intermedias:</strong></li>
          {waypoints.map((point, index) => (
            <li key={index}>{point.location}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RutaDespacho;
