import React, { useState, useEffect } from 'react';
import CryptoJS from 'crypto-js';

function Perfil() {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [rut, setRut] = useState('');
  const [passwordActual, setPasswordActual] = useState('');
  const [nuevoPassword, setNuevoPassword] = useState('');
  const [confirmarPassword, setConfirmarPassword] = useState('');
  const [modoEdicion, setModoEdicion] = useState(false);
  const [modoEdicionPassword, setModoEdicionPassword] = useState(false);

  // Datos adicionales del perfil
  const [patente, setPatente] = useState('');
  const [direccion, setDireccion] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [afp, setAfp] = useState('');
  const [previsionSalud, setPrevisionSalud] = useState('');

  useEffect(() => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const userEmail = localStorage.getItem('userEmail');
    const usuario = users.find(u => u.email === userEmail);

    if (usuario) {
      setNombre(usuario.nombre);
      setEmail(usuario.email);
      setRut(usuario.rut || '');
      setPatente(usuario.patente || '');
      setDireccion(usuario.direccion || '');
      setFechaNacimiento(usuario.fechaNacimiento || '');
      setAfp(usuario.afp || '');
      setPrevisionSalud(usuario.previsionSalud || '');
      setPasswordActual(decryptPassword(usuario.password));  // Mostrar contrase単a desencriptada en el estado
    }
  }, []);

  const handleGuardarDatosIngreso = () => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const userEmail = localStorage.getItem('userEmail');
    const usuarioIndex = users.findIndex(u => u.email === userEmail);

    if (usuarioIndex === -1) {
      alert('Usuario no encontrado');
      return;
    }

    if (
      modoEdicionPassword &&
      (passwordActual !== decryptPassword(users[usuarioIndex].password) || nuevoPassword !== confirmarPassword)
    ) {
      alert('Verifica que la contrase単a actual sea correcta y que las nuevas coincidan.');
      return;
    }

    const encriptarPassword = nuevoPassword
      ? CryptoJS.AES.encrypt(nuevoPassword, 'clave-secreta').toString()
      : users[usuarioIndex].password;

    users[usuarioIndex] = {
      ...users[usuarioIndex],
      nombre,
      email,
      password: encriptarPassword,
    };

    localStorage.setItem('users', JSON.stringify(users));
    localStorage.setItem('userName', nombre);
    localStorage.setItem('userRut', rut);
    localStorage.setItem('userEmail', email);

    alert('Datos de ingreso actualizados correctamente.');
    setModoEdicion(false);
    setModoEdicionPassword(false);
  };

  const handleGuardarPerfilPersonal = () => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const userEmail = localStorage.getItem('userEmail');
    const usuarioIndex = users.findIndex(u => u.email === userEmail);

    if (usuarioIndex === -1) {
      alert('Usuario no encontrado');
      return;
    }

    users[usuarioIndex] = {
      ...users[usuarioIndex],
      rut,
      patente,
      direccion,
      fechaNacimiento,
      afp,
      previsionSalud,
    };

    localStorage.setItem('users', JSON.stringify(users));
    alert('Perfil personal actualizado correctamente.');
  };

  const decryptPassword = (password) => {
    const bytes = CryptoJS.AES.decrypt(password, 'clave-secreta');
    return bytes.toString(CryptoJS.enc.Utf8);
  };

  return (
    <div className="perfil-container">

      {/* Secci坦n 1: Datos de Ingreso */}
      <section className="seccion">
        <h2>Datos de Ingreso</h2>

        <div>
          <label>Nombre:</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} disabled={!modoEdicion} />
        </div>

        <div>
          <label>RUT:</label>
          <input type="text" value={rut} onChange={(e) => setRut(e.target.value)} readOnly />
        </div>

        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} disabled={!modoEdicion} />
        </div>

        {modoEdicionPassword && (
          <>
            <div>
              <label>Contrase単a actual:</label>
              <input type="password" value={passwordActual} onChange={(e) => setPasswordActual(e.target.value)} />
            </div>

            <div>
              <label>Nueva contrase単a:</label>
              <input type="password" value={nuevoPassword} onChange={(e) => setNuevoPassword(e.target.value)} />
            </div>

            <div>
              <label>Confirmar nueva contrase単a:</label>
              <input type="password" value={confirmarPassword} onChange={(e) => setConfirmarPassword(e.target.value)} />
            </div>
          </>
        )}

        {!modoEdicionPassword && (
          <div>
            <label>Contrase単a actual:</label>
            <input type="password" value={passwordActual} onChange={(e) => setPasswordActual(e.target.value)} />
          </div>
        )}

        <button onClick={() => setModoEdicion(!modoEdicion)}>
          {modoEdicion ? 'Cancelar' : 'Editar Datos'}
        </button>

        {modoEdicion && (
          <>
            <button onClick={() => setModoEdicionPassword(!modoEdicionPassword)}>
              {modoEdicionPassword ? 'Cancelar cambio de contrase単a' : 'Cambiar Contrase単a'}
            </button>

            <button onClick={handleGuardarDatosIngreso}>Guardar Cambios</button>
          </>
        )}
      </section>

      {/* Secci坦n 2: Perfil Personal */}
      <section className="seccion">
        <h2>Perfil Personal</h2>

        <div>
          <label>Patente Veh鱈culo:</label>
          <input type="text" value={patente} onChange={(e) => setPatente(e.target.value)} disabled={!modoEdicion} />
        </div>

        <div>
          <label>Direcci坦n:</label>
          <input type="text" value={direccion} onChange={(e) => setDireccion(e.target.value)} disabled={!modoEdicion} />
        </div>

        <div>
          <label>Fecha de Nacimiento:</label>
          <input type="date" value={fechaNacimiento} onChange={(e) => setFechaNacimiento(e.target.value)} disabled={!modoEdicion} />
        </div>

        <div>
          <label>Tipo AFP:</label>
          <input type="text" value={afp} onChange={(e) => setAfp(e.target.value)} disabled={!modoEdicion} />
        </div>

        <div>
          <label>Previsi坦n de Salud:</label>
          <input type="text" value={previsionSalud} onChange={(e) => setPrevisionSalud(e.target.value)} disabled={!modoEdicion} />
        </div>

        <button onClick={handleGuardarPerfilPersonal}>Guardar Perfil Personal</button>
      </section>

    </div>
  );
}

export default Perfil;
