import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, DirectionsRenderer, useJsApiLoader } from '@react-google-maps/api';

const RutaDespacho = () => {
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [origin] = useState('Temuco, Chile'); // Origen de la ruta
  const [destination] = useState('Pucón, Chile'); // Destino de la ruta
  const [waypoints] = useState([
    { location: 'Lican Ray, Chile', stopover: true }, // Parada intermedia 1
    { location: 'Villarrica, Chile', stopover: true }, // Parada intermedia 2
  ]);

  // Carga de la API de Google Maps
  const { isLoaded, google } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyCcF0vvhLLmuLAJcnh2ElM9aLcIwtiiwMo',  // Reemplaza con tu API Key de Google Maps
  });

  // Usamos useCallback para evitar recrear la función en cada render
  const calculateRoute = useCallback(() => {
    if (!origin || !destination || !google) return; // Asegurarnos que `google` esté disponible

    const directionsService = new google.maps.DirectionsService();

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        waypoints: waypoints,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === 'OK') {
          setDirectionsResponse(result);
        } else {
          console.error('Error al calcular la ruta: ', status);
        }
      }
    );
  }, [origin, destination, waypoints, google]);

  useEffect(() => {
    if (isLoaded && google) {
      calculateRoute();
    }
  }, [isLoaded, google, calculateRoute]);

  if (!isLoaded) return <div>Cargando mapa...</div>;

  return (
    <div className="ruta-despacho-container">
      <h2>Ruta de Despacho</h2>
      <GoogleMap
        mapContainerStyle={{ width: '100%', height: '500px' }}
        center={{ lat: -38.73965, lng: -72.58948 }} // Centrado inicial
        zoom={12}
      >
        {directionsResponse && (
          <DirectionsRenderer
            directions={directionsResponse}
            options={{ suppressMarkers: true }} // Opcional: para no mostrar los marcadores predeterminados
          />
        )}
      </GoogleMap>

      {/* Detalles de la ruta */}
      <div className="ruta-info">
        <h3>Detalles de la Ruta:</h3>
        <ul>
          <li><strong>Origen:</strong> {origin}</li>
          <li><strong>Destino:</strong> {destination}</li>
          <li><strong>Paradas intermedias:</strong></li>
          {waypoints.map((point, index) => (
            <li key={index}>{point.location}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RutaDespacho;
