import axios from 'axios';

const API_URL = 'http://localhost:5000';

// Registro
export const signup = async (usuario) => {
  try {
    const response = await axios.post(`${API_URL}/signup`, usuario);
    return response.data;
  } catch (error) {
    console.error('Error en el registro:', error.response ? error.response.data : error.message);
    throw error; // Lanzar el error para que el llamador pueda manejarlo
  }
};

// Login
export const loginUsuario = async (credentials) => {
  try {
    const response = await axios.post(`${API_URL}/login`, credentials);
    return response.data;
  } catch (error) {
    console.error('Error en el inicio de sesión:', error.response ? error.response.data : error.message);
    throw error; // Lanzar el error para que el llamador pueda manejarlo
  }
};

// Obtener perfil
export const obtenerPerfil = async (token) => {
  try {
    const response = await axios.get(`${API_URL}/perfil`, {
      headers: { 'Authorization': token },
    });
    return response.data;
  } catch (error) {
    console.error('Error al obtener el perfil:', error.response ? error.response.data : error.message);
    throw error; // Lanzar el error para que el llamador pueda manejarlo
  }
};
