import React, { useState, useEffect } from 'react';
import { GoogleMap, DirectionsRenderer, useJsApiLoader } from '@react-google-maps/api';

const MapaRuta = () => {
  const [directions, setDirections] = useState(null);
  const [origin, setOrigin] = useState({ lat: -38.73965, lng: -72.58948 });  // Despacho 1
  const [destination, setDestination] = useState({ lat: -38.73559, lng: -72.59386 });  // Despacho 2
  const [routeInfo, setRouteInfo] = useState(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyCcF0vvhLLmuLAJcnh2ElM9aLcIwtiiwMo', // Coloca tu API Key aquí
  });

  useEffect(() => {
    if (isLoaded) {
      const directionsService = new window.google.maps.DirectionsService();

      // Solicitar dirección entre el origen (Despacho 1) y el destino (Despacho 2)
      directionsService.route(
        {
          origin: origin,
          destination: destination,
          travelMode: window.google.maps.TravelMode.DRIVING,  // Puedes cambiar a WALKING, BICYCLING, etc.
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {
            setDirections(result);
            // Obtener información sobre el tiempo estimado de la ruta
            const duration = result.routes[0].legs[0].duration.text;
            setRouteInfo(duration);
          } else {
            console.error('Error al obtener la dirección: ', result);
          }
        }
      );
    }
  }, [isLoaded, origin, destination]);

  // Estilos del mapa
  const containerStyle = {
    width: '100%',
    height: '600px',
  };

  const center = {
    lat: (origin.lat + destination.lat) / 2,  // Centro entre origen y destino
    lng: (origin.lng + destination.lng) / 2,
  };

  if (!isLoaded) return <div>Cargando mapa...</div>;

  return (
    <div className="mapa-ruta">
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={14}>
        {/* Mostrar la ruta si la tenemos */}
        {directions && <DirectionsRenderer directions={directions} />}

        {/* Cuadro flotante con el tiempo estimado */}
        {routeInfo && (
          <div className="route-info">
            <p><strong>Tiempo estimado:</strong> {routeInfo}</p>
            <p><strong>Desde:</strong> Despacho 1</p>
            <p><strong>Hasta:</strong> Despacho 2</p>
          </div>
        )}
      </GoogleMap>
    </div>
  );
};

export default MapaRuta;
