import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsService, DirectionsRenderer } from '@react-google-maps/api';

const Vehiculo2 = () => {
  // Coordenadas de origen y destino para las rutas
  const [origin, setOrigin] = useState({ lat: 40.7128, lng: -74.0060 });  // Nueva York
  const [destination, setDestination] = useState({ lat: 34.0522, lng: -118.2437 });  // Los Angeles
  const [directionsResponse, setDirectionsResponse] = useState(null);

  const containerStyle = {
    width: '100%',
    height: '500px',
  };

  const center = {
    lat: 37.7749,  // Coordenadas de San Francisco (puedes ajustar esto)
    lng: -122.4194,
  };

  // Funci칩n para manejar el c치lculo de direcciones
  const calculateRoute = () => {
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin,
        destination,
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirectionsResponse(result);
        } else {
          console.error('Error al obtener las direcciones:', result);
        }
      }
    );
  };

  return (
    <LoadScript googleMapsApiKey="AIzaSyCcF0vvhLLmuLAJcnh2ElM9aLcIwtiiwMo">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
      >
        {/* Marcadores para origen y destino */}
        <Marker position={origin} />
        <Marker position={destination} />

        {/* Calculando la ruta */}
        {directionsResponse && (
          <DirectionsRenderer directions={directionsResponse} />
        )}

        <button onClick={calculateRoute}>Calcular Ruta</button>
      </GoogleMap>
    </LoadScript>
  );
};

export default Vehiculo2;
