import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsRenderer } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: -34.397, 
  lng: 150.644
};

const Vehiculo2 = () => {
  const [directions, setDirections] = useState(null);

  const origin = { lat: -34.397, lng: 150.644 };  // Punto de origen
  const destination = { lat: -34.507, lng: 150.744 };  // Punto de destino

  useEffect(() => {
    // Llamada a la Directions API de Google Maps para obtener la ruta
    const directionsService = new window.google.maps.DirectionsService();  // Usar correctamente DirectionsService
    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: window.google.maps.TravelMode.DRIVING,  // Puedes cambiar a WALKING, BICYCLING o TRANSIT
      },
      (result, status) => {
        if (status === 'OK') {
          setDirections(result);  // Establece el resultado de la ruta
        } else {
          console.error("Error al obtener la ruta: ", status);
        }
      }
    );
  }, [origin, destination]);  // Agregar 'origin' y 'destination' a las dependencias

  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
      >
        {/* Agregar marcadores para origen y destino */}
        <Marker position={origin} />
        <Marker position={destination} />

        {/* Renderizar la ruta si hay una */}
        {directions && (
          <DirectionsRenderer directions={directions} />
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default Vehiculo2;
