import './App.css';
import { useState, useEffect } from 'react';
import React from 'react';
import { BrowserRouter as Router, Routes, Route , useNavigate} from 'react-router-dom';
import MenuNavegacion from './components/MenuNavegacion';
import MapaNavegacionCliente from './components/MapaNavegacionCliente';
import Inicio from './components/Inicio';
import Vehiculos from './components/Vehiculos';
import Doc from './components/documento';
import Perfil from './components/Perfil';
import Login from './components/login';
import Registro from './components/registro';
import Logout from './components/logout'; 

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const navigate = useNavigate(); // Para la redirección después de logout

    useEffect(() => {
      const token = localStorage.getItem('authToken');
      if (token) {
        setIsAuthenticated(true);
      }
    }, []);

    const handleLogout = () => {
      localStorage.removeItem('authToken');
      localStorage.removeItem('userName');
      localStorage.removeItem('userEmail');
      localStorage.removeItem('usuarioRegistrado');
      setIsAuthenticated(false);

      useNavigate('/dashboard/inicio'); // Redirige a la página de inicio después del logout
    };

  return (
    <Router>
      <MenuNavegacion isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
      <Routes>
        <Route path="/dashboard/mapa" element={<MapaNavegacionCliente />} />
        <Route path="/dashboard/vehiculos" element={<Vehiculos />} />
        <Route path="/dashboard/documento" element={<Doc />} />
        <Route path="/dashboard/perfil" element={<Perfil />} />
        <Route path="/dashboard/inicio" element={<Inicio />} />
        <Route path="/dashboard/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/dashboard/registro" element={<Registro setIsAuthenticated={setIsAuthenticated} />} /> 
        <Route path="/dashboard/logout" element={<Logout handleLogout={handleLogout} />} />
      </Routes>
    </Router>
  );
}

export default App;
