import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function formatearRut(rut) {
  rut = rut.replace(/^0+/, '').replace(/\D/g, '');
  if (rut.length > 1) {
    const cuerpo = rut.slice(0, -1);
    const dv = rut.slice(-1);
    let cuerpoFormateado = '';
    let contador = 0;
    for (let i = cuerpo.length - 1; i >= 0; i--) {
      cuerpoFormateado = cuerpo[i] + cuerpoFormateado;
      contador++;
      if (contador % 3 === 0 && i !== 0) {
        cuerpoFormateado = '.' + cuerpoFormateado;
      }
    }
    return cuerpoFormateado + '-' + dv;
  }
  return rut;
}

function Registro({ setIsAuthenticated, setUserEmail }) {
  const [nombre, setNombre] = useState('');
  const [rut, setRut] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [accesoRestringido, setAccesoRestringido] = useState(false);
  const [emailError, setEmailError] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setAccesoRestringido(true);
      setIsAuthenticated(true);
    }
  }, [setIsAuthenticated]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      alert('Las contraseñas no coinciden');
      return;
    }

    const emailFormateado = email.trim().toLowerCase();
    if (!emailFormateado.endsWith('@conductord.cl') && !emailFormateado.endsWith('@conductorav.cl') && !emailFormateado.endsWith('@adminlog.cl')) {
      setEmailError('El correo debe pertenecer a @conductord.cl, @conductorav.cl o @adminlog.cl');
      return;
    } else {
      setEmailError('');
    }

    const users = JSON.parse(localStorage.getItem('users')) || [];
    const usuarioExistente = users.find(user => user.email === emailFormateado);

    if (usuarioExistente) {
      alert('Este correo ya está registrado');
      return;
    }

    const nuevoUsuario = { nombre, email: emailFormateado, password, rut };
    users.push(nuevoUsuario);

    localStorage.setItem('users', JSON.stringify(users));
    localStorage.setItem('authToken', 'fake-token');
    localStorage.setItem('userName', nombre);
    localStorage.setItem('userEmail', emailFormateado);

    setIsAuthenticated(true);
    setUserEmail(emailFormateado);

    alert('Usuario registrado correctamente');
    navigate('/dashboard/perfil');
  };

  if (accesoRestringido) {
    return (
      <div style={{ textAlign: 'center', marginTop: '2rem' }}>
        <h3>Ya tienes una sesión activa o estás registrado.</h3>
        <p>Redirígete al perfil o cierra sesión para registrar una nueva cuenta.</p>
      </div>
    );
  }

  return (
    <div className="registro-container">
      <form onSubmit={handleSubmit} className="registro-form">
        <h2 className="registro-title">Registro de Usuario</h2>

        <div className="registro-group">
          <label>Nombre completo</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required className="registro-input" />
        </div>

        <div className="registro-group">
          <label>RUT</label>
          <input type="text" value={rut} onChange={(e) => setRut(formatearRut(e.target.value))} required className="registro-input" />
        </div>

        <div className="registro-group">
          <label>Correo electrónico</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="registro-input" />
          {emailError && <p style={{ color: 'red' }}>{emailError}</p>}
        </div>

        <div className="registro-group">
          <label>Contraseña</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="registro-input" />
        </div>

        <div className="registro-group">
          <label>Confirmar contraseña</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required className="registro-input" />
        </div>

        <button type="submit" className="registro-btn">Registrarse</button>
      </form>
    </div>
  );
}

export default Registro;
