import React, { useState, useEffect } from 'react';
import { login } from '../services/userService';
import { useNavigate } from 'react-router-dom';
import '../estilos/login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [mostrarCuadro, setMostrarCuadro] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (mostrarCuadro) {
      setTimeout(() => {
        navigate('/perfil');
      }, 2000);
    }
  }, [mostrarCuadro, navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const { token, user } = await login({ email, password });
      localStorage.setItem('authToken', token);
      localStorage.setItem('userNombre', user.nombre);
      localStorage.setItem('userEmail', user.email);
      localStorage.setItem('userRut', user.rut);

      setMostrarCuadro(true);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al iniciar sesión');
    }
  };

  return (
    <div className="formulario-container">
      <form className="formulario" onSubmit={handleLogin}>
        <h2>Iniciar Sesión</h2>
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <label>Contraseña:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

        {error && <p className="error">{error}</p>}

        <button type="submit">Iniciar Sesión</button>
      </form>

      {mostrarCuadro && (
        <div className="mensaje-login">
          <p>Inicio de sesión exitoso. Redirigiendo...</p>
        </div>
      )}
    </div>
  );
}

export default Login;
