import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Logout({ setIsAuthenticated }) {
  const navigate = useNavigate();

  useEffect(() => {
    // Limpiar el estado global de autenticación y el almacenamiento local
    localStorage.removeItem('authToken');
    localStorage.removeItem('userName');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('usuarioRegistrado');

    setIsAuthenticated(false);  // Actualiza el estado de autenticación global

    // Redirige a la página de inicio después de un pequeño delay
    setTimeout(() => {
      navigate('/dashboard/inicio');
    }, 300); // 300 milisegundos de delay
  }, [navigate, setIsAuthenticated]);

  return null; // No renderiza nada visible
}

export default Logout;
