import React, { useState } from 'react';
import { actualizarPerfil } from '../services/userService';
import '../estilos/perfil.css';

function Perfil() {
  const token = localStorage.getItem('authToken');
  const [nombre, setNombre] = useState(localStorage.getItem('userNombre') || '');
  const [email, setEmail] = useState(localStorage.getItem('userEmail') || '');
  const [rut, setRut] = useState(localStorage.getItem('userRut') || '');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [editando, setEditando] = useState(false);
  const [mensaje, setMensaje] = useState('');

  const handleActualizar = async (e) => {
    e.preventDefault();

    try {
      const response = await actualizarPerfil(token, {
        nombre,
        email,
        rut,
        nuevaPassword,
      });

      // Actualiza localStorage
      localStorage.setItem('userNombre', response.user.nombre);
      localStorage.setItem('userEmail', response.user.email);
      localStorage.setItem('userRut', response.user.rut);

      setMensaje('Perfil actualizado correctamente');
      setEditando(false);
    } catch (error) {
      setMensaje('Error al actualizar perfil');
    }
  };

  return (
    <div className="perfil-container">
      <h2>Perfil del Usuario</h2>
      {mensaje && <p className="mensaje">{mensaje}</p>}
      <form onSubmit={handleActualizar}>
        <label>Nombre:</label>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} disabled={!editando} />

        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} disabled={!editando} />

        <label>RUT:</label>
        <input type="text" value={rut} onChange={(e) => setRut(e.target.value)} disabled={!editando} />

        {editando && (
          <>
            <label>Nueva Contrase√±a:</label>
            <input
              type="password"
              value={nuevaPassword}
              onChange={(e) => setNuevaPassword(e.target.value)}
              placeholder="Dejar en blanco para no cambiar"
            />
            <button type="submit">Guardar Cambios</button>
          </>
        )}
      </form>
      <button onClick={() => setEditando(!editando)}>
        {editando ? 'Cancelar' : 'Editar Perfil'}
      </button>
    </div>
  );
}

export default Perfil;
