import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, DirectionsRenderer, Marker, useJsApiLoader } from '@react-google-maps/api';

const RutaDespacho = () => {
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [routeStarted, setRouteStarted] = useState(false); // Para manejar cuando iniciar la ruta
  const [origin] = useState('Temuco, Chile'); // Origen de la ruta
  const [destination, setDestination] = useState(null); // Destino de la ruta, inicialmente vacío
  const [waypoints] = useState([
    { location: 'Lican Ray, Chile', stopover: true }, // Parada intermedia 1
    { location: 'Villarrica, Chile', stopover: true }, // Parada intermedia 2
  ]);
  const [currentPosition, setCurrentPosition] = useState(null); // Para simular el marcador en movimiento

  // Arreglo con los destinos y sus coordenadas
  const destinations = [
    {
      id: 'despacho1',
      name: 'Pucón, Chile',
      coords: { lat: -39.2713, lng: -71.9403 }
    },
    {
      id: 'despacho2',
      name: 'Concepción, Chile',
      coords: { lat: -36.826, lng: -73.0495 }
    },
    {
      id: 'despacho3',
      name: 'Valdivia, Chile',
      coords: { lat: -39.8045, lng: -73.2459 }
    }
  ];

  // Carga de la API de Google Maps
  const { isLoaded, google } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyCcF0vvhLLmuLAJcnh2ElM9aLcIwtiiwMo', // Reemplaza con tu API Key de Google Maps
    libraries: ['places', 'directions'], // Asegúrate de que sólo se carguen las librerías necesarias
  });

  // Función para calcular la ruta
  const calculateRoute = useCallback(() => {
    if (!origin || !destination || !google) return;

    const directionsService = new google.maps.DirectionsService();

    directionsService.route(
      {
        origin: origin,
        destination: destination.coords, // Usamos las coordenadas del destino
        waypoints: waypoints,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === 'OK') {
          setDirectionsResponse(result); // Almacena la respuesta si la ruta fue exitosa
          setCurrentPosition(result.routes[0].legs[0].start_location); // Inicializa el marcador en el origen
        } else {
          console.error('Error al calcular la ruta: ', status);
        }
      }
    );
  }, [origin, destination, waypoints, google]);

  // Maneja la selección de un destino
  const handleDestinationSelect = (destino) => {
    setDestination(destino); // Cambia el destino basado en la selección
    setRouteStarted(false); // Restablecer el estado de la ruta
    setDirectionsResponse(null); // Limpiar la ruta anterior
  };

  // Inicia el cálculo de la ruta
  const handleStartRoute = () => {
    if (!destination) {
      alert('Por favor, selecciona un destino primero.');
      return;
    }
    setRouteStarted(true); // Marca que la ruta ha sido iniciada
  };

  useEffect(() => {
    if (isLoaded && google && routeStarted) {
      calculateRoute(); // Calcula la ruta una vez que todo esté cargado
    }
  }, [isLoaded, google, routeStarted, calculateRoute]);

  useEffect(() => {
    if (directionsResponse && routeStarted) {
      const totalSteps = directionsResponse.routes[0].legs[0].steps.length;
      let stepIndex = 0;

      const moveMarkerInterval = setInterval(() => {
        if (stepIndex < totalSteps) {
          const step = directionsResponse.routes[0].legs[0].steps[stepIndex];
          setCurrentPosition(step.end_location);
          stepIndex++;
        } else {
          clearInterval(moveMarkerInterval); // Detenemos la simulación cuando se llega al final de la ruta
        }
      }, 2000); // Actualiza la posición del marcador cada 2 segundos

      return () => clearInterval(moveMarkerInterval); // Limpiar el intervalo cuando el componente se desmonte o la ruta cambie
    }
  }, [directionsResponse, routeStarted]);

  if (!isLoaded) return <div>Cargando mapa...</div>;

  return (
    <div className="ruta-despacho-container">
      <h2>Ruta de Despacho</h2>
      <div className="despacho-buttons">
        {destinations.map((destino) => (
          <button
            key={destino.id}
            onClick={() => handleDestinationSelect(destino)}
            disabled={destination?.id === destino.id} // Deshabilitar si ya se seleccionó este destino
          >
            {destino.name}
          </button>
        ))}
      </div>
      <button onClick={handleStartRoute}>Iniciar Ruta</button>

      <GoogleMap
        mapContainerStyle={{ width: '100%', height: '500px' }}
        center={{ lat: -38.73965, lng: -72.58948 }}
        zoom={12}
      >
        {directionsResponse && routeStarted && (
          <>
            <DirectionsRenderer
              directions={directionsResponse}
              options={{
                suppressMarkers: true,
                polylineOptions: {
                  strokeColor: '#FF0000',
                  strokeWeight: 5,
                },
              }} 
            />
            {currentPosition && (
              <Marker
                position={currentPosition}
                icon="http://maps.google.com/mapfiles/ms/icons/red-dot.png" // Puedes personalizar el ícono
              />
            )}
          </>
        )}
      </GoogleMap>

      <div className="ruta-info">
        <h3>Detalles de la Ruta:</h3>
        <ul>
          <li><strong>Origen:</strong> {origin}</li>
          <li><strong>Destino:</strong> {destination ? destination.name : 'No seleccionado'}</li>
          <li><strong>Paradas intermedias:</strong></li>
          {waypoints.map((point, index) => (
            <li key={index}>{point.location}</li>
          ))}
        </ul>

        {directionsResponse && routeStarted && (
          <div>
            <h3>Instrucciones de la Ruta:</h3>
            <ul>
              {directionsResponse.routes[0].legs[0].steps.map((step, index) => (
                <li key={index}>
                  <strong>{step.distance.text}:</strong> {step.instructions}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default RutaDespacho;
